  function P0817HSY950377V3V781BLNX8E9ELG8G5559S45F86B7B0A48B5F2C529F12D1AB5DA996077(post_id){
    var btn = jQuery('#qeditsubmit');
    var btnName = btn.html();
    btn.html('Loading...');
    btn.attr('disabled','true');


    if(post_id==0){
      var post_id = document.getElementById("c_post_id").value;
    }
    var url_status = jQuery("#trtask"+post_id+" .url_status").val();
    if(url_status!=0)return;

    jQuery.ajax({
      type: "GET",
      url: "<?php echo $NVJ9930593I2DG4IQC2YFVH3P9E21PQ5WI709VW92GLHN5816L4F458XZ04R8M97X7089EZFU99570M3D04OU3W1YMYTA83K86670TM5CMHFV10958F8397074089TZ357X9I1X2A6C1951E8DB2FD2403CE0191526C16573009; ?>",
