      dataType: "json",
      beforeSend: function(data){
      },
      success: function(data){
        if(data.status==1){
          jQuery("#qchangeModal").removeClass("fade");
          jQuery('#qchangeModal').modal('hide');

          btn.html(btnName);
          btn.removeAttr('disabled');

          jQuery("#trtask"+post_id).removeClass();
          jQuery("#trtask"+post_id).html(data.html);
          jQuery(".ajaxload").hide();
          jQuery(".ajaxload").addClass("info");
          jQuery(".ajaxload").fadeIn(1000,function (){
            jQuery(".ajaxload").removeClass("ajaxload info");
            jQuery("#qchangeModal").addClass("fade");
          });
