function RJ88B6S1R8RNDR47IIBQ6JF3V9W685FNX419P9M6FL1O9IP9OC9J2H4X480MH0826U7W79MEWBL2W891794F91C387979EFWWBV77J86SPCM1753U08LP6O94H57LD57C8623OQK231O38H681013E6F03F09DCC67B41105625283B0CF1935(id,fetchMethod){
  var cfetchMothod = document.getElementById("fetchMothod"+id).value;
  if(cfetchMothod==fetchMethod)return;

  var btn = jQuery('#changebnt'+id);
  var btnName = btn.html();

  jQuery.ajax({
     type: "GET",
     url: "<?php echo $NVJ9930593I2DG4IQC2YFVH3P9E21PQ5WI709VW92GLHN5816L4F458XZ04R8M97X7089EZFU99570M3D04OU3W1YMYTA83K86670TM5CMHFV10958F8397074089TZ357X9I1X2A6C1951E8DB2FD2403CE0191526C16573009; ?>",
