      btn.html('Loading...');
      btn.attr('disabled','true');
	 }, 
	 success: function(data){

       if(data.status==1){
         jQuery("#trtask"+id).html(data.html);
         jQuery(".ajaxload").hide();
         jQuery(".ajaxload").addClass("info");
         jQuery(".ajaxload").fadeIn(1000,function (){
           jQuery(".ajaxload").removeClass("ajaxload info");
         });


         var badgeManually = jQuery("#badgeManually");
         var badgeAutomatically = jQuery("#badgeAutomatically");

         var Mcount = badgeManually.text();
         var Acount = badgeAutomatically.text();

         if(fetchMethod==1){
           jQuery("#trtask"+id).addClass('active');

           badgeAutomatically.text(parseInt(Acount)+1);
           badgeManually.text(parseInt(Mcount)-1);

         }else{
           jQuery("#trtask"+id).removeClass('active');

           badgeAutomatically.text(parseInt(Acount)-1);
           badgeManually.text(parseInt(Mcount)+1);
         }
       }else{
         //btn.button('reset');
         jQuery('#changebnt'+id).button('reset');
         //document.getElementById("q-task-name").value='';
         jQuery("#ajaxAlertMsg").html(data.html);
         jQuery('#ajaxAlertModal').modal('show');
       }


	 }
 
  });
}

